bloodGlucoseBeforeLunch=#{bloodGlucoseBeforeLunch},bloodGlucoseAfterBreakfast=#{bloodGlucoseAfterBreakfast},bloodGlucoseBeforeBreakfast=#{bloodGlucoseBeforeBreakfast}
